    public static BufferedImage resizeImage(BufferedImage originalImage, int newWidth, int newHeight) {
        BufferedImage resizedImage = new BufferedImage(newWidth, newHeight, originalImage.getType());
        Graphics2D g2d = resizedImage.createGraphics();
        g2d.drawImage(originalImage, 0, 0, newWidth, newHeight, null);
        g2d.dispose();
        return resizedImage;
    }


    public static ByteArrayOutputStream buildBAOS(InputStream fileInputStream) throws IOException {
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        byte[] buf = new byte[16384];
        int len = fileInputStream.read(buf);
        while(len!=-1) {
            baos.write(buf,0,len);
            len = fileInputStream.read(buf);
        }
        return baos;
    }

    public static byte[] bufferedImageToByteArray(BufferedImage image, String extension) {
        try (ByteArrayOutputStream baos = new ByteArrayOutputStream()) {
            javax.imageio.ImageIO.write(image, extension, baos);
            return baos.toByteArray();
        } catch (IOException e) {
            System.out.println("ERROR : IOException dans bufferedImageToByteArray : "+e.getMessage());
            return null;
        }
    }

    public static byte[] picture(byte[] picture, boolean hasAccess, String extension) {

        try {
            BufferedImage image = ImageIO.read(new ByteArrayInputStream(picture));
            if (Objects.isNull(image)){
                throw new IllegalStateException("Image is null... Error BLOB");
            }
            ByteArrayOutputStream baos = new ByteArrayOutputStream();
            if(hasAccess){
                ImageIO.write(image, extension, baos);
                return baos.toByteArray();
            }
            else{
                return CommonUtility.getImageFlou(image);
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    public static InputStream bufferedImageToInputStream(BufferedImage image, String extension) {
        // Convertir BufferedImage en tableau de bytes
        byte[] imageBytes = bufferedImageToByteArray(image, extension);

        // Crer un ByteArrayInputStream  partir du tableau de bytes
        return new ByteArrayInputStream(imageBytes);
    }



